var React = require('react');
var HandleClickMixin = require('utils/HandleClickMixin');
var Route = require('utils/Route');

var AddressSection = require('./AddressSection');
var Background = require('./Background');
var ItemList = require('./ItemList');
var Logo = require('./Logo');
var Menu = require('./Menu');
var SocialInfoSection = require('./SocialInfoSection');

var App = React.createClass({
    mixins: [HandleClickMixin],

    render() {
        console.log("App:render", this.state);

        return (
            <div style={_.container}>
                <Background query="bg-images" images={[]}/>

                <div style={_.sideMenu}>
                    <Logo />
                    <Menu query="categories" categories={[]} />
                    <SocialInfoSection />
                    <AddressSection />
                </div>

                <div style={_.center}>
                    <Route path="items/category/.*">
                        <ItemList items={[]}/>
                    </Route>
                </div>
            </div>
        );
    }
});


var _ = {
    container: {
        height: '100%',
        position: 'relative'
    },
    center: {
        height: '100%',
        marginLeft: 240 // sideMenu.width
    },
    sideMenu: {
        height: '100%',
        left: 0,
        padding: 50,
        position: 'absolute',
        top: 0,
        width: 240
    }
};

module.exports = App;
