import React, {Component, PropTypes} from 'react'

var Img = React.createClass({
    propTypes: {
        src: PropTypes.string.isRequired,
        style: PropTypes.object
    },

    render() {
        return (
            <img style={this.props.style} src={"/" + this.props.src} />
        );
    }
});

export default Img
